int fibonnaci(int n)
{
    if (n == 1)
        return 1;
    if (n == 2)
        return 2;
    return fibonnaci(n - 1) + fibonnaci(n - 2);
}
void ques2()
{
    long int sum = 0, i = 1;
    while (sum < 4000000)
    {
        if (fibonnaci(i) % 2 == 0)
            sum += fibonnaci(i);
        i++;
    }
    cout << sum;
}
