bool isPrime(int n)
{
    for (int i = 2; i * i <= n; i++)
        if (n % i == 0)
            return false;
    return true;
}
void ques7()
{
    int count = 0, i = 2, result;
    while (1)
    {
        if (isPrime(i))
        {
            result = i;
            count++;
        }
        i++;
        if (count == 10001)
            break;
    }
    cout << result;
}
void ques8()
{
    string s = "";
    int product = INT_MIN;
    for (int i = 0; i < s.length() - 13; i++)
    {
        int temp = 1;
        for (int j = i; j < i + 13; j++)
            temp *= (s[j] - 48);
        if (product < temp)
            product = temp;
    }
    cout << product << endl;
}
void ques9()
{
    int n = 1000;
    for (int i = 1; i <= n / 3; i++)
    {
        for (int j = i + 1; j <= n / 2; j++)
        {
            int k = n - i - j;
            if (i * i + j * j == k * k)
            {
                cout << i << " " << j << " " << k << " " << endl
                     << " product is " << i * j * k;
                break;
            }
        }
    }
}
