long int factorial(long int n)
{
    if (n == 0)
        return 1;
    return n * factorial(n - 1);
}
void ques20()
{
    long int fact = factorial(100), result = 0;
    cout << fact << endl;
    while (fact > 0)
    {
        long int b = fact % 10;
        result += b;
        fact /= 10;
    }
    cout << result << endl;
}
int main()
{
    // C++
    ques20();
    return 0;
}
